
public class Demo1_pattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	       int n=5;
	       System.out.println("triangle");
			for(int i=0;i<=n;i++)
			{
				int number = 1;
				
				for(int j=1;j<=i;j++)
				{
					System.out.print(number+ " ");
					number++;
					}
				{
					System.out.println();
				}
			}
		}

	}

