
public class Demo3pattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		   int n=5;
		      int number = 1;
		       System.out.println("triangle");
				for(int i=0;i<n;i++)
				{
					
					
					for(int j=n-i;j>1;j--)
					{
						System.out.print(" ");
					}
					for(int j=0;j<=i;j++)
					{
						System.out.print("* ");
						}
					{
						System.out.println();
					}
				}
			}
	}



